
create table tblcountry (
	pkid bigserial not null,
	txtsortname varchar(3) not null,
	txtname varchar(150) not null,
	txtphonecode varchar(5) not null,
	primary key(pkid)
);


create table tblstate (
  pkid bigserial not null,
  txtname varchar(30) not null,
  fkcountryid bigint not null,
  primary key(pkid),
  constraint foreign_fkcountryid foreign key(fkcountryid) references tblcountry(pkid)
);
create index index_tblstate_fkcountryid on tblstate(fkcountryid);


create table tblcity (
  pkid bigserial not null,
  fkstateid bigint not null,
  txtname varchar(260) not null,
  primary key(pkid),
 constraint foreign_fkstateid foreign key(fkstateid) references tblstate(pkid)
);
create index index_tblcity_fkstateid on tblcity(fkstateid);



